import React, { useRef } from "react";

export default function OfferVibeCheck({ onParseComplete }) {
  const fileInputRef = useRef(null);

  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log(`File selected: ${file.name}`);
      // Replace this with actual parsing logic later
      const mockParsedData = {
        jobDetails:
          "You will work in a cross-functional team on product features.",
        compensation: "$85,000 base salary + 100 RSUs.",
        benefits:
          "Health, dental, vision, and mental health coverage included.",
        redFlags: {
          jobDetails: "No mention of remote policy or probation period.",
          compensation: "RSU vesting schedule not specified.",
          benefits: "No 401(k) or retirement matching listed.",
        },
      };
      onParseComplete(mockParsedData);
    }
  };

  return (
    <div style={styles.body}>
      <h1 style={styles.h1}>CONGRATULATIONS ON THE OFFER!</h1>
      <h2 style={styles.h2}>Now let's break it down...</h2>
      <button style={styles.button} onClick={handleUploadClick}>
        <img src="/uploadicon.png" alt="Upload icon" style={styles.icon} />
        Upload
      </button>
      <input
        type="file"
        ref={fileInputRef}
        style={{ display: "none" }}
        accept="application/pdf, text/plain"
        onChange={handleFileChange}
      />
    </div>
  );
}

const styles = {
  body: {
    backgroundColor: "#000000",
    color: "#ffffff",
    fontFamily: "Jacques Francois, serif",
    textAlign: "center",
    padding: "200px 50px",
    fontSize: "1.5rem",
    lineHeight: "1.6",
    margin: 0,
    textShadow: "4px 4px 8px rgba(0, 0, 0, 0.2), 0 0 30px rgba(187, 187, 187, 0.6)",
    overflow: "hidden",
    height: "100vh",
    height: '100vh',              // takes up full screen
overflow: 'hidden',           // hides any extra scroll
position: 'fixed',            // prevents scroll completely
width: '100%',                // lock horizontal stretch
top: 0, left: 0, 
alignItems: 'center', 
  },
  h1: {
    fontSize: "2.5rem",
    marginBottom: "20px",
    transition: 'all 0.3s ease',
  },
  h2: {
    fontSize: "2rem",
    fontWeight: 400,
    marginTop: "40px",
    marginBottom: "40px",
    transition: "color 0.3s ease, transform 0.2s ease",
  },
  button: {
    backgroundColor: "#e0dede",
    color: "#000000",
    border: "none",
    padding: "16px 32px",
    borderRadius: "30px",
    cursor: "pointer",
    fontSize: "18px",
    boxShadow: "0 8px 20px rgba(0, 0, 0, 0.15)",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "10px",
    transition: "background-color 0.3s ease, transform 0.2s ease",
  },
  icon: {
    height: "25px",
    marginRight: "12px",
    verticalAlign: "middle",
  },
};
