import React from 'react';

export default function OfferBreakdown({ data }) {
  const jobDetails = data?.jobDetails || "Explore the key responsibilities, work environment, qualifications.";
  const compensation = data?.compensation || "Competitive salary packages, bonuses, and stock options.";
  const benefits = data?.benefits || "Flexible working hours, health insurance, development programs.";

  return (
    <div style={styles.body}>
      <h1 style={styles.h1}>OFFER BREAKDOWN</h1>
      <h2 style={styles.h2}>Job Title</h2>

      <div style={styles.container}>
        <OfferBox title="Job Details" content={jobDetails} flagText={data?.redFlags?.jobDetails} />
        <OfferBox title="Compensation" content={compensation} flagText={data?.redFlags?.compensation} />
        <OfferBox title="Benefits" content={benefits} flagText={data?.redFlags?.benefits} />
      </div>
    </div>
  );
}

function OfferBox({ title, content, flagText }) {
  return (
    <div style={styles.rectangle} className="rectangle">
      <h2 style={styles.h2}>{title}</h2>
      <p>{content}</p>
      <div style={styles.flag} className="flag" tabIndex={0}></div>
      {flagText && (
        <div style={styles.flagTooltip} className="flag-tooltip">
          <strong>{title}:</strong>
          <br />
          {flagText}
        </div>
      )}
    </div>
  );
}

const styles = {
  body: {
    backgroundColor: '#000000',
    color: 'white',
    fontFamily: 'Jacques Francois, serif',
    textAlign: 'center',
    padding: '50px',
    overflow: 'hidden',
    margin: 0,
    minHeight: '100vh'
  },
  h1: {
    fontSize: '2.5rem',
    marginBottom: '20px',
    marginTop: '20px'
  },
  h2: {
    fontSize: '2.5rem',
    marginBottom: '20px',
    marginTop: '1px'
  },
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignContent: 'center',
    gap: '60px',
    marginTop: '50px',
    flexWrap: 'wrap'
  },
  rectangle: {
    position: 'relative',
    width: '400px',
    height: '500px',
    backgroundColor: '#1c1c1c',
    border: '2px solid #b7fdaa',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
    borderRadius: '20px',
    fontSize: '1.2rem',
    padding: '30px',
    boxSizing: 'border-box',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease'
  },
  flag: {
    position: 'absolute',
    top: '40px',
    right: '-20px',
    width: '20px',
    height: '120px',
    backgroundColor: '#d93025',
    borderRadius: '6px 0 0 6px',
    cursor: 'pointer',
    boxShadow: '0 0 8px rgba(217, 48, 37, 0.8)',
    transition: 'background-color 0.3s ease'
  },
  flagTooltip: {
    position: 'absolute',
    top: '40px',
    right: '25px',
    backgroundColor: '#d93025',
    color: 'white',
    padding: '12px 18px',
    borderRadius: '12px',
    fontSize: '1rem',
    width: '220px',
    boxShadow: '0 0 15px rgba(217, 48, 37, 0.9)',
    zIndex: 1000
  }
};
